import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { emphasize, fade, darken, lighten } from '@material-ui/core/styles/colorManipulator';

import clsx from 'clsx';
import service from './../service/UserService';
import { useHistory } from 'react-router-dom';
import { TextValidator } from 'react-material-ui-form-validator';

import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';


const useStyles = makeStyles((theme) => ({
  section: {
    position: "absolute",
    top: "55%",
    left: "50%",
    transform: "translate(-50%, -50%)",
  }
}
));

export default function Component(props) {

  const [firstName, setFirstName] = useState();
  const [lastName, setLastName] = useState();
  const [username, setUsermame] = useState();
  const [password, setPassword] = useState();
  const [email, setEmail] = useState();
  const [company, setCompany] = useState();

  let history = useHistory();

  const handleSubmit = async (event) => {

    const json = JSON.stringify({
      "firstName": firstName,
      "lastName": lastName,
      "userCredentials": {
        "username": username,
        "password": password,
      },
      "email": email,
      "company": company
    });

    let status = await service.registerUser(json);
    event.preventDefault();

    if(status != "") {
      history.push("/input");
    }
    else {
      alert("There was a problem processing your request. Please try again later.");
    }
  }

  const handleChange = (event) => {
    const email = event.target.value;
    this.setState({ email });
}

  const onChangeFirstName = (event) => {
    setFirstName(event.target.value);
  }
  const onChangeLastName = (event) => {
    setLastName(event.target.value);
  }
  const onChangeUsername = (event) => {
    setUsermame(event.target.value);
  }
  const onChangePassword = (event) => {
    setPassword(event.target.value);
  }
  const onChangeEmail = (event) => {
    setEmail(event.target.value);
  }
  const onChangeCompany = (event) => {
    setCompany(event.target.value);
  }

  const classes = useStyles();

  return (
    <section className={classes.section}>
      <Container maxWidth="xs">
        <Box pt={8} pb={10}>
          <Box mb={3} textAlign="center">
            <Typography variant="h5" component="h2">Create a new account</Typography>
          </Box>
