                  <TextField variant="outlined" required fullWidth value={firstName} onChange={onChangeFirstName} name="firstName" id="firstName" label="First name" autoComplete="fname" />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField variant="outlined" required fullWidth value={lastName} onChange={onChangeLastName} name="lastName" id="lastName" label="Last name" autoComplete="lname" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={username} onChange={onChangeUsername} name="username" id="username" label="Username" autoComplete="current-username" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" type="password" required fullWidth value={password} onChange={onChangePassword} name="password" id="password" label="Password" autoComplete="current-password" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" type="email" required fullWidth value={email} onChange={onChangeEmail} name="email" id="email" label="Email address" autoComplete="email" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={company} onChange={onChangeCompany} name="company" id="company" label="Company name" />
                </Grid>
                <Grid item xs={12}>
                  <FormControlLabel control={<Checkbox required name="terms" value="1" color="primary" />} label="I agree to the terms of use and privacy policy." />
                </Grid>
              </Grid>
              <Box my={2}>
                <Button type="button" onClick={handleSubmit} fullWidth variant="contained" color="primary">
                  Sign up
                </Button>
              </Box>
              <Box textAlign="center">
                <Link to="/login" variant="body2" style={{ textDecoration: 'none' }}>Already have an account? Sign in</Link>
              </Box>
            </form> */}

            <ValidatorForm
                ref="form"
                onSubmit={this.handleSubmit}
                onError={errors => console.log(errors)}
            >
                <TextValidator
                    label="Email"
                    onChange={this.handleChange}
                    name="email"
                    value={email}
                    validators={['required', 'isEmail']}
                    errorMessages={['this field is required', 'email is not valid']}
                />
                <Button type="submit">Submit</Button>
            </ValidatorForm>

          </Box>
        </Box>
      </Container>
    </section>
  );
}
