import React, { useState } from 'react';

import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import { useHistory } from 'react-router-dom';

export default function Component(props) {

  const [directCost, setDirectCost] = useState();
  const [indirectCost, setIndirectCost] = useState();
  const [intangibleCost, setIntangibleCost] = useState();
  const [opportunityCost, setOpportunityCost] = useState();
  const [potentialRiskCost, setPotentialRiskCost] = useState();

  let history = useHistory();

  const handleSubmit = async () => {

    const json = JSON.stringify({
      "directCost": directCost,
      "indirectCost": indirectCost,
      "intangibleCost": intangibleCost,
      "opportunityCost": opportunityCost,
      "potentialRiskCost": potentialRiskCost
    });

    // let status = await service.loginUser(json);
    // event.preventDefault();

    // if(status != "") {
    //   history.push("/input");
    // }
    // else {
    //   alert("An account with that username and password could not be found.");
    // }

  }

  const onChangeDirectCost = (event) => {
    setDirectCost(event.target.value);
  }
  const onChangeIndirectCost = (event) => {
    setIndirectCost(event.target.value);
  }
  const onChangeIntangibleCost = (event) => {
    setIntangibleCost(event.target.value);
  }
  const onChangeOpportunityCost = (event) => {
    setOpportunityCost(event.target.value);
  }
  const onChangePotentialRiskCost = (event) => {
    setPotentialRiskCost(event.target.value);
  }

  return (
    <section>
      <Container maxWidth="xs">
        <Box pt={8} pb={10}  align="right">
          <Box mb={3} textAlign="center">
            <Typography variant="h5" component="h2">Cost Streams</Typography>
          </Box>
          <Box>

            <ValidatorForm onSubmit={handleSubmit} onError={errors => console.log(errors)}>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth 
                    label="Direct Cost *" 
                    onChange={onChangeDirectCost} 
                    name="directCost"
                    value={directCost}
                    validators={['required', 'matchRegexp:^[0-9]+$]']}
                    errorMessages={['This field is required', 'Must be a valid number']}
                    />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth 
                    label="Indirect Cost *" 
                    onChange={onChangeIndirectCost} 
                    name="indirectCost"
                    value={indirectCost}
                    validators={['required', 'matchRegexp:^[0-9]+$]']}
                    errorMessages={['This field is required', 'Must be a valid number']}
                    />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth 
                    label="Intangible Cost" 
                    onChange={onChangeIntangibleCost} 
                    name="intangibleCost"
                    value={intangibleCost}
                    validators={['matchRegexp:^[0-9]+$]']}
                    errorMessages={['Must be a valid number']}
                    />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth 
                    label="Opportunity Cost" 
                    onChange={onChangeOpportunityCost} 
                    name="opportunityCost"
                    value={opportunityCost}
                    validators={['matchRegexp:^[0-9]+$]']}
                    errorMessages={['Must be a valid number']}
                    />
               </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth 
                    label="Potential Risk Cost" 
                    onChange={onChangePotentialRiskCost} 
                    name="potentialRiskCost"
                    value={potentialRiskCost}
                    validators={['matchRegexp:^[0-9]+$]']}
                    errorMessages={['Must be a valid number']}
                    />
                </Grid>
              </Grid>
            </ValidatorForm>

          </Box>
        </Box>
      </Container>
    </section>
  );
}
