import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { emphasize, fade, darken, lighten } from '@material-ui/core/styles/colorManipulator';

import clsx from 'clsx';
import service from './../service/UserService';
import { useHistory } from 'react-router-dom';
import { ValidatorForm, TextValidator, CheckboxValidatorElement } from 'react-material-ui-form-validator';

import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';

import InputAdornment from "@material-ui/core/InputAdornment";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import IconButton from "@material-ui/core/IconButton";


const useStyles = makeStyles((theme) => ({
  section: {
    position: "absolute",
    top: "55%",
    left: "50%",
    transform: "translate(-50%, -50%)",
  },
  root: {
    display: "flex",
    flexWrap: "wrap"
  },
  margin: {
    margin: theme.spacing(1)
  },
  withoutLabel: {
    marginTop: theme.spacing(3)
  },
  textField: {
    width: "25ch"
  }
}
));

export default function Component(props) {

  const [firstName, setFirstName] = useState();
  const [lastName, setLastName] = useState();
  const [username, setUsermame] = useState();
  const [password, setPassword] = useState();
  const [email, setEmail] = useState();
  const [company, setCompany] = useState();

  let history = useHistory();

  const handleSubmit = async (event) => {

    const json = JSON.stringify({
      "firstName": firstName,
      "lastName": lastName,
      "userCredentials": {
        "username": username,
        "password": password,
      },
      "email": email,
      "company": company
    });

    let status = await service.registerUser(json);
    event.preventDefault();

    if (status != "") {
      history.push("/input");
    }
    else {
      alert("There was a problem processing your request. Please try again later.");
    }
  }

  const onChangeFirstName = (event) => {
    setFirstName(event.target.value);
  }
  const onChangeLastName = (event) => {
    setLastName(event.target.value);
  }
  const onChangeUsername = (event) => {
    setUsermame(event.target.value);
  }
  const onChangePassword = (event) => {
    setPassword(event.target.value);
  }
  const onChangeEmail = (event) => {
    setEmail(event.target.value);
  }
  const onChangeCompany = (event) => {
    setCompany(event.target.value);
  }

  const classes = useStyles();

  const [values, setValues] = React.useState({
    password: "",
    showPassword: false
  });

  const handleChange = (prop) => (event) => {
    setValues({ values, [prop]: event.target.value });
  };

  const handleClickShowPassword = () => {
    setValues({ values, showPassword: !values.showPassword });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  return (
    <section className={classes.section}>
      <Container maxWidth="xs">
        <Box pt={8} pb={10}>
          <Box mb={3} textAlign="center">
            <Typography variant="h5" component="h2">Create a new account</Typography>
          </Box>
          <Box>
            {/* <form validate="true" >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField variant="outlined" required fullWidth value={firstName} onChange={onChangeFirstName} name="firstName" id="firstName" label="First name" autoComplete="fname" />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField variant="outlined" required fullWidth value={lastName} onChange={onChangeLastName} name="lastName" id="lastName" label="Last name" autoComplete="lname" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={username} onChange={onChangeUsername} name="username" id="username" label="Username" autoComplete="current-username" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" type="password" required fullWidth value={password} onChange={onChangePassword} name="password" id="password" label="Password" autoComplete="current-password" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" type="email" required fullWidth value={email} onChange={onChangeEmail} name="email" id="email" label="Email address" autoComplete="email" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={company} onChange={onChangeCompany} name="company" id="company" label="Company name" />
                </Grid>
                <Grid item xs={12}>
                  <FormControlLabel control={<Checkbox required name="terms" value="1" color="primary" />} label="I agree to the terms of use and privacy policy." />
                </Grid>
              </Grid>
              <Box my={2}>
                <Button type="button" onClick={handleSubmit} fullWidth variant="contained" color="primary">
                  Sign up
                </Button>
              </Box>
              <Box textAlign="center">
                <Link to="/login" variant="body2" style={{ textDecoration: 'none' }}>Already have an account? Sign in</Link>
              </Box>
            </form> */}

            <ValidatorForm onSubmit={handleSubmit} onError={errors => console.log(errors)} >
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextValidator variant="outlined" fullWidth
                    label="First Name *"
                    onChange={onChangeFirstName}
                    name="firstName"
                    value={firstName}
                    validators={['required', 'matchRegexp:^[a-zA-Z]+$', 'matchRegexp:^.{0,15}$']}
                    errorMessages={['This field is required', 'Can only contain letters', 'Must be 2 to 15 characters']}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextValidator variant="outlined" fullWidth
                    label="Last Name *"
                    onChange={onChangeLastName}
                    name="lastName"
                    value={lastName}
                    validators={['required', 'matchRegexp:^[a-zA-Z]+$', 'matchRegexp:^.{0,15}$']}
                    errorMessages={['This field is required', 'Can only contain letters', 'Must be 2 to 15 characters']}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth
                    label="Username *"
                    onChange={onChangeUsername}
                    name="username"
                    value={username}
                    validators={['required', 'matchRegexp:^[a-zA-Z0-9]+$', 'matchRegexp:^.{8,32}$']}
                    errorMessages={['This field is required', 'Can only contain letters and numbers', 'Must be 8 to 32 characters']}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth type={values.showPassword ? "text" : "password"}
                    label="Password *"
                    onChange={onChangePassword}
                    onChange={handleChange("password")}
                    name="password"
                    // value={password}
                    value={values.password}
                    validators={['required', 'matchRegexp:^.{8,32}$', 'matchRegexp:^(?=.*[0-9])(?=.*[a-z])(?=.*[A-Z])(?=.*[*.!@$%^&(){}[]:;<>,.?/~_+-=|).{8,32}$']}
                    errorMessages={['This field is required', 'Must be 8 to 32 characters', 'Minimum of one uppercase, lowercase, number, and symbol']}
                    endadornment={
                      <InputAdornment position="end">
                        <IconButton
                          aria-label="toggle password visibility"
                          onClick={handleClickShowPassword}
                          onMouseDown={handleMouseDownPassword}
                          edge="end"
                        >
                          {values.showPassword ? <Visibility /> : <VisibilityOff />}
                        </IconButton>
                      </InputAdornment>
                    }
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth
                    label="Email *"
                    onChange={onChangeEmail}
                    name="email"
                    value={email}
                    validators={['required', 'isEmail']}
                    errorMessages={['This field is required', 'Invalid email']}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth
                    label="Company *"
                    onChange={onChangeCompany}
                    name="company"
                    value={company}
                    validators={['required']}
                    errorMessages={['This field is required']}
                  />
                </Grid>
              </Grid>
              <Box my={2}>
                <Button type="submit" fullWidth variant="contained" color="primary">Sign up</Button>
              </Box>
              <Box textAlign="center">
                <Link to="/login" variant="body2" style={{ textDecoration: 'none' }}>Already have an account? Sign in</Link>
              </Box>
            </ValidatorForm>
          </Box>
        </Box>
      </Container>
    </section>
  );
}
