import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { emphasize, fade, darken, lighten } from '@material-ui/core/styles/colorManipulator';

import clsx from 'clsx';
import service from './../service/UserService';

import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Button from '@material-ui/core/Button';
import { Link } from 'react-router-dom';


const useStyles = makeStyles((theme) => ({
  section: {
    position: "absolute",
    top: "55%",
    left: "50%",
    transform: "translate(-50%, -50%)",
  },
  actions: {
    alignText: "center",
    [theme.breakpoints.down('sm')]: {
      marginTop: theme.spacing(4)
    },
  }
}
));

export default function Component(props) {
  const [firstName, setFirstName] = useState();
  const [lastName, setLastName] = useState();
  const [username, setUsermame] = useState();
  const [password, setPassword] = useState();
  const [email, setEmail] = useState();
  const [company, setCompany] = useState();

  const handleSubmit = () => {

    const json = JSON.stringify({
      "firstName": firstName,
      "lastName": lastName,
      "userCredentials": {
        "username": username,
        "password": password,
      },
      "email": email,
      "company": company
    });

    service.registerUser(json);
  }

  const onChangeFirstName = (event) => {
    setFirstName(event.target.value);
  }
  const onChangeLastName = (event) => {
    setLastName(event.target.value);
  }
  const onChangeUsername = (event) => {
    setUsermame(event.target.value);
  }
  const onChangePassword = (event) => {
    setPassword(event.target.value);
  }
  const onChangeEmail = (event) => {
    setEmail(event.target.value);
  }
  const onChangeCompany = (event) => {
    setCompany(event.target.value);
  }

  const classes = useStyles();

  return (
    <section className={classes.section}>
      <Container maxWidth="xs">
        <Box pt={8} pb={10}>
          <Box mb={3} textAlign="center">
            <Typography variant="h5" component="h2">Create a new account</Typography>
          </Box>
          <Box>
            <form onSubmit={handleSubmit} validate="true">
              <Grid container spacing={2}>
                <Grid item xs={12} sm={6}>
                  <TextField variant="outlined" required fullWidth value={firstName} onChange={onChangeFirstName} name="firstName" id="firstName" label="First name" autoComplete="fname" />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField variant="outlined" required fullWidth value={lastName} onChange={onChangeLastName} name="lastName" id="lastName" label="Last name" autoComplete="lname" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={username} onChange={onChangeUsername} name="username" id="username" label="Username" autoComplete="current-username" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" type="password" required fullWidth value={password} onChange={onChangePassword} name="password" id="password" label="Password" autoComplete="current-password" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" type="email" required fullWidth value={email} onChange={onChangeEmail} name="email" id="email" label="Email address" autoComplete="email" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={company} onChange={onChangeCompany} name="company" id="company" label="Company name" />
                </Grid>
                <Grid item xs={12}>
                  <FormControlLabel control={<Checkbox required name="terms" value="1" color="primary" onChange={onChangeTerms} />} label="I agree to the terms of use and privacy policy." />
                </Grid>
              </Grid>
              <Box my={2}>
                <Button type="submit" fullWidth variant="contained" color="primary">
                  Sign up
            </Button>
              </Box>
              <Box textAlign="right">
                <Link to="/login" variant="body2" style={{ textDecoration: 'none' }}>Already have an account? Sign in</Link>
              </Box>
            </form>
          </Box>
        </Box>
      </Container>
    </section>
  );
}
