import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { emphasize, fade, darken, lighten } from '@material-ui/core/styles/colorManipulator';

import clsx from 'clsx';
import service from './../service/UserService';
import { useHistory } from "react-router-dom";
import { ValidatorForm, TextValidator } from 'react-material-ui-form-validator';
import CircularProgress from '@material-ui/core/CircularProgress';

import Container from '@material-ui/core/Container';
import Box from '@material-ui/core/Box';
import { Link } from 'react-router-dom';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';


const useStyles = makeStyles((theme) => ({
  section: {
    position: "absolute",
    top: "45%",
    left: "50%",
    transform: "translate(-50%, -50%)",
  },
  actions: {
    alignText: "center",
    [theme.breakpoints.down('sm')]: {
      marginTop: theme.spacing(4)
    },
  },
  root: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  button: {
    margin: theme.spacing(2),
  },
  placeholder: {
    height: 40,
  },
}
));

export default function Component(props) {

  const [username, setUsermame] = useState();
  const [password, setPassword] = useState();

  let history = useHistory();

  const handleSubmit = async () => {

    const json = JSON.stringify({
      "firstName": "",
      "lastName": "",
      "userCredentials": {
        "username": username,
        "password": password,
      },
      "email": "",
      "company": ""
    });

    <CircularProgress className={classes.spin} />

    let status = await service.loginUser(json);
    event.preventDefault();

    if (status != "") {
      history.push("/input");
    }
    else {
      alert("An account with that username and password could not be found.");
    }

  }

  const onChangeUsername = (event) => {
    setUsermame(event.target.value);
  }
  const onChangePassword = (event) => {
    setPassword(event.target.value);
  }

  const classes = useStyles();

  const [query, setQuery] = React.useState('idle');
  const timerRef = React.useRef();

  React.useEffect(
    () => () => {
      clearTimeout(timerRef.current);
    },
    [],
  );

  const handleClickQuery = () => {
    clearTimeout(timerRef.current);

    if (query !== 'idle') {
      setQuery('idle');
      return;
    }

    setQuery('progress');
    timerRef.current = window.setTimeout(() => {
      setQuery('success');
    }, 2000);
  }

  return (
    <section className={classes.section}>
      <Container maxWidth="xs">
        <Box pt={8} pb={10}>
          <Box mb={3} textAlign="center">
            <Typography variant="h5" component="h2">Sign in</Typography>
          </Box>
          <Box>
            {/* <form validate="true">
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={username} onChange={onChangeUsername} name="username" id="username" label="Username" autoComplete="current-username" />
                </Grid>
                <Grid item xs={12}>
                  <TextField variant="outlined" required fullWidth value={password} onChange={onChangePassword} name="password" id="password" label="Password" type="password" autoComplete="current-password" />
                </Grid>
              </Grid>
              <Box my={2}>
                <Button type="button" onClick={handleSubmit} fullWidth variant="contained" color="primary">Sign in</Button>
              </Box>
              <Box textAlign="center">
                <Link to="/register" variant="body2" style={{ textDecoration: 'none' }}>Don't have an account? Register.</Link>
              </Box>
            </form> */}

            <ValidatorForm onSubmit={handleSubmit} onError={errors => console.log(errors)}>
              <Grid container spacing={2}>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth
                    label="Username *"
                    onChange={onChangeUsername}
                    name="username"
                    value={username}
                    validators={['required']}
                    errorMessages={['This field is required']}
                  />
                </Grid>
                <Grid item xs={12}>
                  <TextValidator variant="outlined" fullWidth type="password"
                    label="Password *"
                    onChange={onChangePassword}
                    name="password"
                    value={password}
                    validators={['required']}
                    errorMessages={['This field is required']}
                  />
                </Grid>
              </Grid>
              <Box my={2}>
                <Button type="submit" fullWidth variant="contained" color="primary">Login</Button>
              </Box>
              <Box textAlign="center">
                <Link to="/register" variant="body2" style={{ textDecoration: 'none' }}>Don't have an account? Register</Link>
              </Box>
            </ValidatorForm>

            <div className={classes.placeholder}>
              {query === 'success' ? (
                <Typography>Success!</Typography>
              ) : (
                  <Fade
                    in={query === 'progress'}
                    style={{
                      transitionDelay: query === 'progress' ? '800ms' : '0ms',
                    }}
                    unmountOnExit
                  >
                    <CircularProgress />
                  </Fade>
                )}
            </div>
            <Button onClick={handleClickQuery} className={classes.button}>
              {query !== 'idle' ? 'Reset' : 'Simulate a load'}
            </Button>


          </Box>
        </Box>
      </Container>
    </section>
  );
}
